<script>
import Header from './components/Header.vue'
import SliderHome from './components/SliderHome.vue'
import Products from './components/Products.vue'
import Subscribe from './components/Subscribe.vue'
import We from './components/We.vue'
import Testimonials from './components/Testimonials.vue'
import Blog from './components/Blog.vue'
import Instagram from './components/Instagram.vue'
import Footer from './components/Footer.vue'

export default {
  name: 'App',
  components: {
    Header,
    SliderHome,
    Products,
    Subscribe,
    We,
    Testimonials,
    Blog,
    Instagram,
    Footer
  },

  mounted () {
    window.addEventListener('scroll', this.checkScrollSpeed())
  },

  methods: {
    checkScrollSpeed (settings) {
      settings = settings || {}

      let lastPos
      let newPos
      let timer
      let delta
      const delay = settings.delay || 10

      const clear = () => {
        lastPos = null
        delta = 0
        document.documentElement.style.setProperty('--velocity-y-down', '0%')
      }

      clear()

      return () => {
        newPos = window.scrollY
        if (lastPos != null) {
          delta = newPos - lastPos
        }
        lastPos = newPos
        clearTimeout(timer)
        timer = setTimeout(clear, delay)
        if (delta > 0) {
          if (delta > 120) {
            delta = 120
          }
          document.documentElement.style.setProperty('--velocity-y-down', delta / 1.5 + '%')
        }
        return delta
      }
    }
  }
}
</script>

<style lang="scss">
img {
  display: block;
}
.enable-js {
  display: none;
  font-size: .8em;
  margin-bottom: .8em;
  text-align: center;
  width: 100%;
  position: absolute;
  background-color: #ef4d2f;
  color: #fff;
  padding: 10px;
}
:root.no-js {
  .enable-js {
    display: block;
  }
}
:root {
  --velocity-y-down: 0;
  --gutter: 20px;
}

* {
  scroll-behavior: smooth;
  scrollbar-color: #7f7f7f #dfdfdf;
  scrollbar-width: thin;
}
@media (prefers-reduced-motion) {
  * {
    scroll-behavior: auto;
  }
}
*::-webkit-scrollbar {
  width: 12px;
}
*::-webkit-scrollbar-track {
  background: #dfdfdf;
}
*::-webkit-scrollbar-thumb {
  background-color: #7f7f7f;
  border: 3px solid #dfdfdf;
}
body {
  font-family: 'Poppins', sans-serif;
  font-weight: 300;
  line-height: 1.2;
  color: $color-black;
  @include interpolate(font-size, 320px, 1480px, 16px, 20px);
}
.section {
  @include interpolate(margin-bottom, 320px, 1480px, 60px, 120px);
  &__header {
    grid-column: 1/-1;
    text-align: center;
    @include interpolate(margin-bottom, 320px, 1480px, 20px, 40px);
  }
}
.slider {
  &__wrapper {
    display: grid;
    grid-gap: calc(var(--gutter) / 2);
    grid-template-columns: 10px;
    grid-template-rows: minmax(150px, 1fr);
    grid-auto-flow: column;
    grid-auto-columns: calc(50% - var(--gutter) * 2);
    overflow: auto hidden;
    -webkit-overflow-scrolling: touch;
    overscroll-behavior-x: contain;
    &:before,
    &:after {
      content: '';
      width: 10px;
    }
  }
}

.dropdown {
